struct Transaction {
	uint256 txType;
	uint256 from;
	uint256 to;
	uint256 ergsLimit;
	uint256 ergsPerPubdataByteLimit;
	uint256 maxFeePerErg;
	uint256 maxPriorityFeePerErg;
	uint256 paymaster;
